Scripts.OnUpdate:
if ( UBI_CHECK_DATE ~= date( "%Y%m%d" ) ) then
    UBI_CHECK_DATE = date( "%Y%m%d" );
    UberInventory_MailExpirationCheck();
end;

Scripts.OnLoad:
_G[ self:GetName().. "Text" ]:SetFormattedText( UBI_OPTION_ALPHA, self:GetValue()*100 );
_G[ self:GetName().."Low" ]:SetText( "0 %" );
_G[ self:GetName().."High" ]:SetText( "100 %" );
self:SetMinMaxValues( 0, 1 );
self:SetValueStep( 0.1 );

_G[ self:GetName().. "Text" ]:SetFormattedText( UBI_OPTION_MINIMAP, self:GetValue() );
_G[ self:GetName().."High" ]:SetText( "360" );
_G[ self:GetName().."Low" ]:SetText( "0" );
self:SetMinMaxValues( 0, 360 );
self:SetValueStep( 1 );

Scripts.OnShow:
-- Add options to the list
UIDropDownMenu_Initialize( self, UberInventory_Locations_Init );

-- By default select first one from the list
UIDropDownMenu_SetText( self, UBI_LocationList[UBI_FILTER_LOCATIONS].value );

-- Set width
UIDropDownMenu_SetWidth( self, 120 );

-- Add options to the list
UIDropDownMenu_Initialize( self, UberInventory_Quality_Init );

-- By default select first one from the list
UIDropDownMenu_SetText( self, UBI_QUALITY[UBI_FILTER_QUALITY] );

-- Set width
UIDropDownMenu_SetWidth( self, 102 );

-- Add options to the list
UIDropDownMenu_Initialize( self, UberInventory_Classes_Init );

-- By default select first one from the list
UIDropDownMenu_SetText( self, UBI_CLASSES[UBI_FILTER_CLASSES] );

-- Set width
UIDropDownMenu_SetWidth( self, 120 );

self:SetChecked( UBI_FILTER_USABLE );
_G[ self:GetName() .. "Text" ]:SetText( UBI_USABLE_ITEMS );
_G[ self:GetName() .. "Text" ]:ClearAllPoints();
_G[ self:GetName() .. "Text" ]:SetPoint( "RIGHT", self, "LEFT", -4, 0 );
